import Keycloak from 'keycloak-js';
import axios from 'axios';

// Keycloak initialization options
const keycloakConfig = {
  url: 'http://localhost:8080',
  realm: 'areti-alliance',
  clientId: 'areti-crm-client'
};

// Initialize Keycloak
const keycloak = new Keycloak(keycloakConfig);

// Authentication service
const AuthService = {
  // Initialize Keycloak
  init: () => {
    return new Promise((resolve, reject) => {
      keycloak.init({
        onLoad: 'check-sso',
        silentCheckSsoRedirectUri: window.location.origin + '/silent-check-sso.html',
        pkceMethod: 'S256'
      })
        .then(authenticated => {
          if (authenticated) {
            // Exchange Keycloak token for app session token
            AuthService.exchangeToken().then(() => {
              resolve(authenticated);
            }).catch(err => {
              console.error('Token exchange error:', err);
              resolve(false);
            });
          } else {
            resolve(authenticated);
          }
        })
        .catch(error => {
          console.error('Keycloak init error:', error);
          reject(error);
        });
    });
  },

  // Log in
  login: () => {
    keycloak.login();
  },

  // Log out
  logout: () => {
    // Clear local tokens
    localStorage.removeItem('aretiUser');
    // Logout from Keycloak
    keycloak.logout();
  },

  // Check if authenticated
  isAuthenticated: () => {
    return !!keycloak.token;
  },

  // Exchange Keycloak token for app session token
  exchangeToken: async () => {
    try {
      const response = await axios.post('http://localhost:4000/auth/token', {
        token: keycloak.token
      });

      const { token } = response.data;
      
      // Parse token to get user info
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const payload = JSON.parse(window.atob(base64));
      
      // Store in localStorage
      localStorage.setItem('aretiUser', JSON.stringify({
        email: payload.email,
        name: payload.name,
        role: payload.role,
        token: token,
        timestamp: new Date().getTime()
      }));
      
      return token;
    } catch (error) {
      console.error('Token exchange error:', error);
      throw error;
    }
  },

  // Get user profile
  getUserProfile: () => {
    const user = localStorage.getItem('aretiUser');
    return user ? JSON.parse(user) : null;
  },

  // Check if token is valid
  hasValidToken: () => {
    const user = localStorage.getItem('aretiUser');
    if (!user) return false;
    
    const userData = JSON.parse(user);
    const now = new Date().getTime();
    // Check if token is expired (24 hour validity)
    return now - userData.timestamp < 24 * 60 * 60 * 1000;
  },

  // Get authorization header
  getAuthHeader: () => {
    const user = localStorage.getItem('aretiUser');
    if (!user) return {};
    
    const userData = JSON.parse(user);
    return {
      'Authorization': `Bearer ${userData.token}`
    };
  }
};

export default AuthService;
